<script>
import { mapGetters, mapActions} from 'vuex';

export default {
    data () {
        return {
            show: false,
            opacitySlider: 50
        }
    },
    computed: {
        ...mapGetters({
            getSizeSlider: "projects/getSizeSlider",
            getCameraPosition: "projects/getCameraPosition",
        }),
        sizeSlider: {
            get() {
                return this.getSizeSlider
            },
            set(newValue) {
                this.setSizeSlider(newValue)
            }
        }
    },
    methods: {
        ...mapActions({
            setSizeSlider: "projects/setSizeSlider",
            setCameraPosition: "projects/setCameraPosition"
        }),
        formatOpacity(val) {
            return val / 100;
        }
    }
}
/* 

    setCameraPosition
    1 - left
    2 - right
    3 - top
    4 - freeCamera
